mapper.update:
UPDATE machine
SET machine_name = #{machineName},
machine_time = CURRENT_TIMESTAMP,  
machine_status = #{machineStatus}
WHERE machine_no = #{machineNo}

mapper.select:
SELECT machine_no, machine_name, machine_time, machine_status
FROM machine
WHERE machine_no = #{machineNo}

SELECT machine_status
FROM machine
WHERE machine_no = #{machineNo}
